body.script:
function toggleMenu(id, event) {
  event.stopPropagation();
  var menu = document.getElementById(id);
  document.querySelectorAll('.share-popup-menu').forEach(function(m) {
    if(m.id !== id) m.style.display = 'none';
  });
  menu.style.display = (menu.style.display === 'block') ? 'none' : 'block';
}

function shareFB(url) {
  window.open("https://www.facebook.com/sharer/sharer.php?u=" + encodeURIComponent(url), "_blank", "width=600,height=400");
}

function shareTW(url) {
  window.open("https://twitter.com/intent/tweet?url=" + encodeURIComponent(url), "_blank", "width=600,height=400");
}

function copyLink(url) {
  var dummy = document.createElement("input");
  document.body.appendChild(dummy);
  dummy.value = url;
  dummy.select();
  document.execCommand("copy");
  document.body.removeChild(dummy);
  alert("Bu postun linki kopyalandı!");
}

window.onclick = function() {
  document.querySelectorAll('.share-popup-menu').forEach(function(m) { m.style.display = 'none'; });
}

